function ic_apply_range_slider(a){if(typeof a.ionRangeSlider!=="function"){return}var r=1;var e=a.data("unit");var i=a.data("min");var t=a.data("max");var n=a.data("current-min");var o=a.data("current-max");if(t-i<2){r=.1}a.ionRangeSlider({type:"double",min:i,max:t,from:n,to:o,step:r,postfix:" "+e,grid:true,hide_min_max:true,onFinish:function(){var r=a.closest("form");r.submit();a.closest(".product_size_filter").addClass("active")}})}jQuery(document).ready(function(a){a("body").on("reload",".product-size-filter-container",function(){var r=a(this);r.removeClass("toReload");r.find(".ic-range-slider").each(function(){var r=a(this);ic_apply_range_slider(r)})});a(".product-size-filter-container.toReload").trigger("reload")});